{
  config,
  lib,
  pkgs,
  ...
}:
let
  nvimInitLua = ./init.lua;
  nvimPackLockJson = ./nvim-pack-lock.json;
in
{
  programs.neovim = {
    enable = true;
    package = pkgs.neovim;
    defaultEditor = true;
    vimAlias = true;
    vimdiffAlias = true;
  };

  home.file.".config/nvim/init.lua" = {
    source = nvimInitLua;
    force = true;
  };

  home.file.".config/nvim/lua" = {
    source = ./lua;
    recursive = true;
    force = true;
  };

  home.activation.copyNvimPackLock = config.lib.dag.entryAfter [ "writeBoundary" ] ''
    $DRY_RUN_CMD mkdir -p "$HOME/.config/nvim"
    $DRY_RUN_CMD cp -f ${nvimPackLockJson} "$HOME/.config/nvim/nvim-pack-lock.json"
    $DRY_RUN_CMD chmod 644 "$HOME/.config/nvim/nvim-pack-lock.json"
  '';
}

-- Fuzzy finder with extensible pickers for files, buffers, and more.
-- From: https://github.com/nvim-telescope/telescope.nvim
local telescope = require("telescope")
-- Theme helpers used by the dropdown/ivy pickers.
-- From: https://github.com/nvim-telescope/telescope.nvim
local telescope_themes = require("telescope.themes")
telescope.setup({
	defaults = {
		prompt_prefix = "   ",
		selection_caret = " ❯ ",
		entry_prefix = "   ",
		multi_icon = "+ ",
		path_display = { "filename_first" },
		vimgrep_arguments = {
			"rg",
			"--color=never",
			"--no-heading",
			"--with-filename",
			"--line-number",
			"--column",
			"--smart-case",
			"--sort=path",
		},
	},
	pickers = {
		find_files = {
			theme = "ivy",
		},
	},
	extensions = {
		fzf = {
			fuzzy = true,
			override_generic_sorter = true,
			override_file_sorter = true,
			case_mode = "smart_case",
		},
		["ui-select"] = {
			telescope_themes.get_dropdown(),
		},
	},
})
telescope.load_extension("gh")
telescope.load_extension("fzf")
telescope.load_extension("ui-select")

local function ivy(iopts)
	return telescope_themes.get_ivy(iopts)
end

-- Telescope builtin pickers used in the keymaps below.
-- From: https://github.com/nvim-telescope/telescope.nvim
local builtin = require("telescope.builtin")
local keymap = vim.keymap.set
local opts = { noremap = true, silent = true }

-- @keymap <C-p>: Find files (Telescope)
keymap("n", "<C-p>", function()
	builtin.find_files(ivy({
		find_command = {
			"fd",
			"--type",
			"f",
			"--strip-cwd-prefix",
			"--hidden",
		},
	}))
end, opts)

-- @keymap <leader>of: Open old files (Telescope)
keymap("n", "<leader>of", function()
	builtin.oldfiles(ivy({
		only_cwd = true,
	}))
end, opts)

-- @keymap <leader>lg: Live grep (Telescope)
keymap("n", "<leader>lg", function()
	builtin.live_grep(ivy())
end, opts)

-- @keymap <leader>fb: Find buffers (Telescope)
keymap("n", "<leader>fb", function()
	builtin.buffers(ivy())
end, opts)

-- @keymap <leader>fh: Find help tags (Telescope)
keymap("n", "<leader>fh", function()
	builtin.help_tags(ivy())
end, opts)

-- @keymap <leader>fc: Find commands (Telescope)
keymap("n", "<leader>fc", function()
	builtin.commands(ivy())
end, opts)

-- @keymap <leader>fr: Resume last Telescope search
keymap("n", "<leader>fr", function()
	builtin.resume(ivy())
end, opts)

-- @keymap <leader>fq: Find in quickfix (Telescope)
keymap("n", "<leader>fq", function()
	builtin.quickfix(ivy())
end, opts)

-- @keymap <leader>/: Fuzzy find in current buffer (Telescope)
keymap("n", "<leader>/", function()
	builtin.current_buffer_fuzzy_find(ivy())
end, opts)

-- @keymap <leader>ghi: Find GitHub issues (Telescope)
keymap("n", "<leader>ghi", function()
	telescope.extensions.gh.issues(ivy())
end, opts)

-- Tests for lua/plugins.lua
-- Tests plugin management and setup patterns

describe("plugins", function()
	describe("vim.pack API", function()
		it("should have vim.pack table or be nil in older nvim", function()
			-- vim.pack is available in Neovim 0.10+
			local has_pack = vim.pack ~= nil
			assert.is_boolean(has_pack)
		end)

		it("should have add function if pack API exists", function()
			if vim.pack then
				assert.is_function(vim.pack.add)
			else
				assert.is_true(true) -- Skip on older Neovim
			end
		end)
	end)

	describe("plugin autocommands", function()
		it("should support BufWritePost for lint", function()
			local autocmds = vim.api.nvim_get_autocmds({ event = "BufWritePost" })
			assert.is_table(autocmds)
		end)
	end)

	describe("formatters_by_ft pattern", function()
		it("should be able to create filetype-based config", function()
			local formatters = {
				lua = { "stylua" },
				python = { "black" },
				go = { "gofmt" },
			}
			assert.is_table(formatters)
			assert.are.same({ "stylua" }, formatters.lua)
			assert.are.same({ "black" }, formatters.python)
			assert.are.same({ "gofmt" }, formatters.go)
		end)
	end)

	describe("plugin setup pattern", function()
		it("should support setup with empty table", function()
			-- Common pattern: require("plugin").setup({})
			-- We test the pattern works
			local mock_setup_called = false
			local mock_plugin = {
				setup = function(opts)
					mock_setup_called = true
					assert.is_table(opts)
				end,
			}
			mock_plugin.setup({})
			assert.is_true(mock_setup_called)
		end)

		it("should support setup with options", function()
			local received_opts = nil
			local mock_plugin = {
				setup = function(opts)
					received_opts = opts
				end,
			}
			mock_plugin.setup({ enabled = true, timeout = 500 })
			assert.equals(true, received_opts.enabled)
			assert.equals(500, received_opts.timeout)
		end)
	end)

	describe("gitsigns pattern", function()
		it("should support signs configuration", function()
			local signs_config = {
				add = { text = "+" },
				change = { text = "~" },
				delete = { text = "_" },
			}
			assert.equals("+", signs_config.add.text)
			assert.equals("~", signs_config.change.text)
		end)
	end)
end)

