      "Bash(osascript:*)",
      "Bash(pip:*)",
      "Bash(pip3:*)",
      "Bash(pnpm:*)",
      "Bash(scutil:*)",
      "Bash(timeout:*)",
      "Bash(uv:*)",
      "Bash(uvx:*)",
      "Bash(yarn:*)",
      "mcp__serena",
      "WebFetch(domain:docs.anthropic.com)"
    ],
    "deny": [
      "Bash(sudo:*)",
      "Bash(rm -rf /*:*)",
      "Bash(rm -rf ~/*:*)",
      "Bash(chmod -R 777:*)",
      "Bash(mkfs:*)",
      "Bash(dd if=:*)",
      "Bash(git push --force origin main:*)",
      "Bash(git push --force origin master:*)",
      "Bash(git push -f origin main:*)",
      "Bash(git push -f origin master:*)",
      "Bash(git push --force-with-lease origin main:*)",
      "Bash(git push --force-with-lease origin master:*)"
    ],
    "read": {
      "deny": [
        "**/.env",
        "**/.env.local",
        "**/*.pem",
        "**/*.key",
        "**/secrets/**",
        "**/credentials/**",
        "**/.aws/**",
        "**/.ssh/**",
        "**/docker-compose*.yml",
        "**/config/database.yml"
