          {
            "type": "command",
            "command": "$HOME/.claude/notify.sh",
            "timeout": 10,
            "async": true
          },
          {
            "type": "command",
            "command": "$HOME/.claude/pushover.sh",
            "timeout": 10,
            "async": true
          }
        ]
      }
