    "allow": [
      "Bash(bun:*)",
      "Bash(cargo:*)",
      "Bash(cat:*)",
      "Bash(cp:*)",
      "Bash(docker-compose:*)",
      "Bash(docker:*)",
      "Bash(env)",
      "Bash(gh api:*)",
      "Bash(gh auth:*)",
      "Bash(gh issue:*)",
      "Bash(gh pr checkout:*)",
      "Bash(gh pr checks:*)",
      "Bash(gh pr create:*)",
      "Bash(gh pr edit:*)",
      "Bash(gh pr list:*)",
      "Bash(gh pr merge:*)",
      "Bash(gh pr review:*)",
      "Bash(gh pr view:*)",
      "Bash(gh release:*)",
      "Bash(gh repo:*)",
      "Bash(gh run list:*)",
      "Bash(gh run view:*)",
      "Bash(gh run watch:*)",
      "Bash(git add:*)",
      "Bash(git branch:*)",
      "Bash(git checkout:*)",
      "Bash(git cherry-pick:*)",
      "Bash(git commit:*)",
      "Bash(git diff:*)",
      "Bash(git fetch:*)",
      "Bash(git log:*)",
      "Bash(git merge:*)",
      "Bash(git pull:*)",
      "Bash(git push:*)",
      "Bash(git rebase:*)",
      "Bash(git reset:*)",
      "Bash(git stash:*)",
      "Bash(git status:*)",
      "Bash(git tag:*)",
      "Bash(git worktree:*)",
      "Bash(grep:*)",
      "Bash(kubectl:*)",
      "Bash(lsof:*)",
      "Bash(make:*)",
      "Bash(mkdir:*)",
      "Bash(nix-build:*)",
      "Bash(nix-env:*)",
      "Bash(nix-shell:*)",
      "Bash(nix:*)",
      "Bash(npm:*)",
      "Bash(osascript:*)",
      "Bash(pip:*)",
      "Bash(pip3:*)",
      "Bash(pnpm:*)",
      "Bash(scutil:*)",
      "Bash(timeout:*)",
      "Bash(uv:*)",
      "Bash(uvx:*)",
      "Bash(yarn:*)",
      "mcp__serena",
      "WebFetch(domain:docs.anthropic.com)"
    ],
    "deny": [
      "Bash(sudo:*)",
      "Bash(rm -rf /*:*)",
      "Bash(rm -rf ~/*:*)",
      "Bash(chmod -R 777:*)",
      "Bash(mkfs:*)",
      "Bash(dd if=:*)",
      "Bash(git push --force origin main:*)",
      "Bash(git push --force origin master:*)",
      "Bash(git push -f origin main:*)",
      "Bash(git push -f origin master:*)",
      "Bash(git push --force-with-lease origin main:*)",
      "Bash(git push --force-with-lease origin master:*)"
    ],
    "read": {
      "deny": [
        "**/.env",
        "**/.env.local",
        "**/*.pem",
        "**/*.key",
        "**/secrets/**",
        "**/credentials/**",
        "**/.aws/**",
        "**/.ssh/**",
        "**/docker-compose*.yml",
        "**/config/database.yml"
      ]
    },
