          {
            "type": "command",
            "command": "command -v dcg >/dev/null 2>&1 && dcg",
            "timeout": 5,
            "async": true
          },
          {
            "type": "command",
            "command": "$HOME/.claude/security.sh",
            "timeout": 5
          },
          {
            "type": "command",
            "command": "$HOME/.claude/notify.sh",
            "timeout": 10,
            "async": true
          },
          {
            "type": "command",
            "command": "$HOME/.claude/pushover.sh",
            "timeout": 10,
            "async": true
          }
        ]
      },
      {
        "matcher": "Write|Edit|MultiEdit",
        "hooks": [
          {
            "type": "command",
            "command": "git-ai checkpoint claude --hook-input stdin"
          }
        ]
      }
    ],
    "PostToolUse": [
      {
        "matcher": "Write|Edit|MultiEdit",
        "hooks": [
          {
            "type": "command",
            "command": "git-ai checkpoint claude --hook-input stdin"
          }
        ]
      }
    ],
    "Notification": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "$HOME/.claude/notify.sh",
            "timeout": 10,
            "async": true
          },
          {
            "type": "command",
            "command": "$HOME/.claude/pushover.sh",
            "timeout": 10,
            "async": true
          }
        ]
      }
    ],
    "Stop": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "$HOME/.claude/notify.sh",
            "timeout": 10,
            "async": true
          },
          {
            "type": "command",
            "command": "$HOME/.claude/pushover.sh",
            "timeout": 10,
            "async": true
          }
        ]
      }
    ],
    "SessionEnd": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "$HOME/.claude/notify.sh",
            "timeout": 10,
            "async": true
          },
          {
            "type": "command",
            "command": "$HOME/.claude/pushover.sh",
            "timeout": 10,
            "async": true
          }
        ]
      }
    ],
    "PreCompact": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "$HOME/.claude/notify.sh",
            "timeout": 10,
            "async": true
          },
          {
            "type": "command",
            "command": "$HOME/.claude/pushover.sh",
            "timeout": 10,
            "async": true
          }
        ]
      }
    ],
    "SubagentStop": [
      {
        "hooks": [
          {
            "type": "command",
            "command": "$HOME/.claude/notify.sh",
            "timeout": 10,
            "async": true
          },
          {
            "type": "command",
            "command": "$HOME/.claude/pushover.sh",
            "timeout": 10,
            "async": true
          }
        ]
      }
    ]
  },
  "statusLine": {
    "type": "command",
    "command": "$HOME/.claude/statusline-git.sh"
  },
  "teammateMode": "tmux",
  "defaultMode": "bypassPermissions"
}

