    home.activation.binShells = lib.hm.dag.entryAfter [ "writeBoundary" ] ''
      /run/wrappers/bin/sudo mkdir -p /bin
      /run/wrappers/bin/sudo ln -sf ${pkgs.bash}/bin/bash /bin/bash
